    // 토큰발급
    public String getToken(String id, String password) throws Exception {
        String api = "/v1/auth";
        
        HashMap<String, String> account = new HashMap<String, String>();
        account.put("id", id);
        account.put("password", password);

        Request request = new Request.Builder()
            .url(SIMPLESIGN_URL + api)
            .post(RequestBody.create(JSON, mapper.writeValueAsString(account)))
            .build();
        Response response = client.newCall(request).execute();
        JSONObject contents = getContent(response.body().string(), "contents");
        
        System.out.println("getToken : "+contents);
        
        return contents.get("access_token").toString();
    }
    
