            final ArrayList<HashMap<String, String>> signers = new ArrayList<>();
            signers.add(
                new HashMap<String, String>(){{
                    put("name", "관리자");
                    put("email", "admin@hrdkorea.or.kr");
                }}
            );

            // 필수값 patterns []
