    // 대량 서명문서 목록 조회
    // 대량 요청 키는 겹치지 않음. 따라서 결과값은 하나만 출력
    public List<Map<String, Object>> getSignatureList(List<String> reqSetKeyList, String token) throws Exception {
    	
    	JSONArray jsonArray = null;
    	JSONObject arrayObject = null;
    	List<JSONObject> contentsArray = new ArrayList<>();
    	List<Map<String, Object>> contentsList = new ArrayList<>();
    	
    	for(int i=0; i < reqSetKeyList.size(); i++) {
    		String reqSetKey = reqSetKeyList.get(i).toString().trim();
    		
    		System.out.println("#ehoh > reqSetKey : "+reqSetKey);
    		
	    	String api = "/v1/patterns/" + reqSetKey;
	        Request request = new Request.Builder()
	                .url(SIMPLESIGN_URL + api)
	                .addHeader("Authorization", "Bearer " + token)
	                .get()
	                .build();
	        Response response = client.newCall(request).execute();
	        String data = response.body().string();
	        
	        System.out.println("#ehoh > data check : "+data);
	        
	    	JSONObject jsonObject = (JSONObject) parser.parse(data);
	    	
	    	jsonArray = (JSONArray) jsonObject.get("contents");
	    	
	    	// 배열 각 객체에 접근
	    	for (Object obj : jsonArray) {
    			arrayObject = (JSONObject) obj;
    			contentsArray.add(arrayObject);
    		}
	    	
	    	String id = contentsArray.get(i).get("id").toString();
	    	String title = contentsArray.get(i).get("title").toString();
	    	String progressStatus = contentsArray.get(i).get("progress").toString();
	    	String useYn = contentsArray.get(i).get("useYn").toString();
	    	String viewUrl = SIMPLESIGN_URL + contentsArray.get(i).get("viewUrl").toString();
	    	
	    	boolean useYnCheck = useYn.equals("true");
	    	boolean progressCheck = progressStatus.equals("PROGRESS");
	    	boolean completeCheck = progressStatus.equals("COMPLETE");
	    	
	    	// return 데이터 put
	    	Map<String, Object> contents = new HashMap<>();
	    	
	    	if(useYnCheck && progressCheck) {
	    		contents.put("id", id);
	    		contents.put("title", title);
	    		contents.put("progressStatus", progressStatus);
	    		
	    		contentsList.add(contents);
	    	}else if(useYnCheck && completeCheck) {
	    		contents.put("id", id);
	    		contents.put("title", title);
	    		contents.put("progressStatus", progressStatus);
	    		contents.put("viewUrl", viewUrl);
	    		
	    		contentsList.add(contents);
	    		
	    	}
    	}
    	
        return contentsList;
    }
    
