mapper.select:
SELECT GUBUN,
INSTT_IDX, INSTT_NM,
0 AS INSTT_IDX, '전체' AS INSTT_NM,
SUM(DGNS) AS DGNS, SUM(QESTNR) AS QESTNR, SUM(BSISBNSL) AS BSISBNSL, SUM(AI_STD_BPR) AS AI_STD_BPR, SUM(AI_STD_SOJT) AS AI_STD_SOJT, SUM(AI_SLF_BPR) AS AI_SLF_BPR, SUM(AI_SLF_SOJT) AS AI_SLF_SOJT, SUM(FIT_BPR) AS FIT_BPR, SUM(FIT_SOJT) AS FIT_SOJT, SUM(FIT_SOJT_SITE) AS FIT_SOJT_SITE, SUM(RSLT_SNST) AS RSLT_SNST, SUM(RSLT_APPLC) AS RSLT_APPLC, SUM(DEP_DGNS) AS DEP_DGNS, SUM(EDU_TR_SYS) AS EDU_TR_SYS, SUM(SITE_PRC) AS SITE_PRC FROM (SELECT * FROM ( SELECT '기업수' AS GUBUN, TO_CHAR(SYSDATE,'YYYY') AS STATISTICS_YEAR, TO_CHAR(SYSDATE,'MM') AS STATISTICS_MONTH, TO_CHAR(#{INSTT_IDX}) AS INSTT_IDX, TO_CHAR((SELECT INSTT_NAME FROM HRD_COM_INSTT WHERE INSTT_IDX = #{INSTT_IDX})) AS INSTT_NM, (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_DGNS_BSC A, HRD_BSK C WHERE TO_CHAR(ISSUE_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX} ) AS DGNS, (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_DGNS_QUSTNR_RSLT A, HRD_DGNS_BSC B, HRD_BSK C WHERE A.BSC_IDX = B.BSC_IDX AND TO_CHAR(A.ISSUE_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND B.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX} ) AS QESTNR, (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_DGNS_BSISCNSL A, HRD_BSK C WHERE STATUS = '1' AND TO_CHAR(ISSUE_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX}) AS BSISBNSL, (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_DEVLOP A, HRD_DEVLOP_TP B, HRD_BSK C WHERE A.DEVLOP_IDX = B.DEVLOP_IDX AND B.PRTBIZ_IDX = 4 AND A.TP_DEVLOP_SE = 'use' AND A.CONFM_STATUS = '55' AND TO_CHAR(A.LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX} ) AS AI_STD_BPR, (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_DEVLOP A, HRD_DEVLOP_TP B, HRD_BSK C WHERE A.DEVLOP_IDX = B.DEVLOP_IDX AND B.PRTBIZ_IDX = 1 AND A.TP_DEVLOP_SE = 'use' AND A.CONFM_STATUS = '55' AND TO_CHAR(A.LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX} ) AS AI_STD_SOJT, (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_DEVLOP A, HRD_DEVLOP_TP B, HRD_BSK C WHERE A.DEVLOP_IDX = B.DEVLOP_IDX AND B.PRTBIZ_IDX = 4 AND A.TP_DEVLOP_SE IN ('reuse','new') AND A.CONFM_STATUS = '55' AND TO_CHAR(A.LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX} ) AS AI_SLF_BPR, (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_DEVLOP A, HRD_DEVLOP_TP B, HRD_BSK C WHERE A.DEVLOP_IDX = B.DEVLOP_IDX AND B.PRTBIZ_IDX = 1 AND A.TP_DEVLOP_SE IN ('reuse','new') AND A.CONFM_STATUS = '55' AND TO_CHAR(A.LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX} ) AS AI_SLF_SOJT, (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL A, HRD_BSK C WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 1 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX} ) AS FIT_BPR, (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL A, HRD_BSK C WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 2 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX} ) AS FIT_SOJT, (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL A, HRD_BSK C WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 3 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX} ) AS FIT_SOJT_SITE, (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL_RSLT_ANALS A, HRD_BSK C WHERE TR_RESULT_REPRT_SE = 1 AND STATUS = 1 AND ISDELETE = 0 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX} ) AS RSLT_SNST, (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL_RSLT_ANALS A, HRD_BSK C WHERE TR_RESULT_REPRT_SE = 2 AND STATUS = 1 AND ISDELETE = 0 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX} ) AS RSLT_APPLC, (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL A, HRD_BSK C WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 4 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX} ) AS DEP_DGNS, (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL A, HRD_BSK C WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 5 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX} ) AS EDU_TR_SYS, (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL A, HRD_BSK C WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 6 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX} ) AS SITE_PRC FROM DUAL UNION ALL SELECT '과정수' as GUBUN, TO_CHAR(SYSDATE,'YYYY') AS STATISTICS_YEAR, TO_CHAR(SYSDATE,'MM') AS STATISTICS_MONTH, TO_CHAR(#{INSTT_IDX}) AS INSTT_IDX, TO_CHAR((SELECT INSTT_NAME FROM HRD_COM_INSTT WHERE INSTT_IDX = #{INSTT_IDX})) AS INSTT_NM, (SELECT COUNT(*) FROM HRD_DGNS_BSC A, HRD_BSK C WHERE TO_CHAR(ISSUE_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX}) AS DGNS, (SELECT COUNT(*) FROM HRD_DGNS_QUSTNR_RSLT A, HRD_DGNS_BSC B, HRD_BSK C WHERE A.BSC_IDX = B.BSC_IDX AND TO_CHAR(A.ISSUE_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND B.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX}) AS QESTNR, (SELECT COUNT(*) FROM HRD_DGNS_BSISCNSL A, HRD_BSK C WHERE STATUS = '1' AND TO_CHAR(ISSUE_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX}) AS BSISBNSL, (SELECT COUNT(*) FROM HRD_DEVLOP A, HRD_DEVLOP_TP B, HRD_BSK C WHERE A.DEVLOP_IDX = B.DEVLOP_IDX AND B.PRTBIZ_IDX = 4 AND A.TP_DEVLOP_SE = 'use' AND A.CONFM_STATUS = '55' AND TO_CHAR(A.LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX}) AS AI_STD_BPR, (SELECT COUNT(*) FROM HRD_DEVLOP A, HRD_DEVLOP_TP B, HRD_BSK C WHERE A.DEVLOP_IDX = B.DEVLOP_IDX AND B.PRTBIZ_IDX = 1 AND A.TP_DEVLOP_SE = 'use' AND A.CONFM_STATUS = '55' AND TO_CHAR(A.LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX}) AS AI_STD_SOJT, (SELECT COUNT(*) FROM HRD_DEVLOP A, HRD_DEVLOP_TP B, HRD_BSK C WHERE A.DEVLOP_IDX = B.DEVLOP_IDX AND B.PRTBIZ_IDX = 4 AND A.TP_DEVLOP_SE IN ('reuse','new') AND A.CONFM_STATUS = '55' AND TO_CHAR(A.LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX}) as AI_SLF_BPR, (SELECT COUNT(*) FROM HRD_DEVLOP A, HRD_DEVLOP_TP B, HRD_BSK C WHERE A.DEVLOP_IDX = B.DEVLOP_IDX AND B.PRTBIZ_IDX = 1 AND A.TP_DEVLOP_SE IN ('reuse','new') AND A.CONFM_STATUS = '55' AND TO_CHAR(A.LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX}) as AI_SLF_SOJT, (SELECT COUNT(*) FROM HRD_CNSL A, HRD_BSK C WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 1 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX}) AS FIT_BPR, (SELECT COUNT(*) FROM HRD_CNSL A, HRD_BSK C WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 2 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX}) AS FIT_SOJT, (SELECT COUNT(*) FROM HRD_CNSL A, HRD_BSK C WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 3 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM') AND A.BPL_NO = C.BPL_NO AND C.PRI_SUP_CD = 1 AND C.INSTT_IDX = #{INSTT_IDX}) AS FIT_SOJT_SITE, NULL AS RSLT_SNST, NULL AS RSLT_APPLC, NULL AS DEP_DGNS, NULL AS EDU_TR_SYS, NULL AS SITE_PRC FROM DUAL UNION ALL SELECT GUBUN, TO_CHAR(STATISTICS_YEAR), TO_CHAR(STATISTICS_MONTH), TO_CHAR(INSTT_IDX), TO_CHAR(INSTT_NM), DGNS, QESTNR, BSISBNSL, AI_STD_BPR, AI_STD_SOJT, AI_SLF_BPR, AI_SLF_SOJT, FIT_BPR, FIT_SOJT, FIT_SOJT_SITE, RSLT_SNST, RSLT_APPLC, DEP_DGNS, EDU_TR_SYS, SITE_PRC FROM IFS_HRD_TOTAL_STATISTICS )) WHERE 1=1 AND INSTT_IDX = #{INSTT_IDX}
AND STATISTICS_MONTH IN (
TO_NUMBER(#{dataItem})
)
AND STATISTICS_YEAR = TO_CHAR(SYSDATE, 'YYYY')
AND STATISTICS_YEAR = #{YEAR}
GROUP BY GUBUN , INSTT_IDX, INSTT_NM ORDER BY GUBUN DESC

SELECT CASE WHEN (SLF_TR_BPR ='Y' OR SLF_TR_SOJT='Y' OR SLF_TR_SOJT_SITE='Y' OR SLF_TR_WLB='Y' OR CNSGN_TR_BRP ='Y' OR CNSGN_TR_CNSRTM='Y' OR CNSGN_TR_RIB='Y' ) THEN 'Y' ELSE 'N' END AS PARTC_YN, CASE WHEN (SLF_TR_BPR ='Y' OR SLF_TR_SOJT='Y' OR SLF_TR_SOJT_SITE='Y' OR SLF_TR_WLB='Y') THEN 'Y' ELSE 'N' END AS SLF_TR, CASE WHEN (CNSGN_TR_BRP ='Y' OR CNSGN_TR_CNSRTM='Y' OR CNSGN_TR_RIB='Y' ) THEN 'Y' ELSE 'N' END AS CNSGN_TR, TOT.* FROM ( SELECT TO_CHAR(SYSDATE,'YYYY') AS STATISTICS_YEAR, TO_CHAR(SYSDATE,'MM') AS STATISTICS_MONTH, A.INSTT_IDX AS INSTT_IDX, (SELECT INSTT_NAME FROM HRD_COM_INSTT C WHERE C.INSTT_IDX = A.INSTT_IDX) AS INSTT_NM, (SELECT LISTAGG(PRVTCNTR_NAME,', ') WITHIN GROUP(ORDER BY PRVTCNTR_NAME) AS PRVTCNTR_NAME FROM (SELECT PRVTCNTR_NAME FROM HRD_COM_PRVTCNTR C WHERE C.MEMBER_IDX IN (SELECT MEMBER_IDX FROM HRD_SPTJ_AGREM D WHERE D.BPL_NO = A.BPL_NO))) AS PRVTCNTR_NM, CASE WHEN A.PRI_SUP_CD = '2' THEN '대규모기업' WHEN A.PRI_SUP_CD = '1' THEN '우선지원기업' WHEN A.PRI_SUP_CD IS NULL THEN '-' END AS CORP_TYPE, A.BPL_NM AS CORP_NM, A.BPL_NO AS BPL_NO, 'Y' AS DGNS, CASE WHEN (SELECT COUNT(DISTINCT A.BPL_NO) FROM HRD_DGNS_QUSTNR_RSLT C, HRD_DGNS_BSC D WHERE C.BSC_IDX = D.BSC_IDX AND TO_CHAR(C.ISSUE_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND D.BPL_NO = A.BPL_NO ) = 0 THEN 'N' ELSE 'Y' END AS QESTNR, CASE WHEN (SELECT COUNT(*) FROM HRD_DGNS_BSISCNSL C WHERE STATUS = '1' AND TO_CHAR(ISSUE_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = 0 THEN 'N' ELSE 'Y' END AS BSISBNSL, CASE WHEN (SELECT COUNT(*) FROM HRD_DEVLOP C, HRD_DEVLOP_TP D WHERE C.DEVLOP_IDX = D.DEVLOP_IDX AND D.PRTBIZ_IDX = 4 AND C.TP_DEVLOP_SE = 'use' AND C.CONFM_STATUS = '55' AND TO_CHAR(C.LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND C.BPL_NO = A.BPL_NO) = 0 THEN 'N' ELSE 'Y' END as AI_STD_BPR, CASE WHEN (SELECT COUNT(*) FROM HRD_DEVLOP C, HRD_DEVLOP_TP D WHERE C.DEVLOP_IDX = D.DEVLOP_IDX AND D.PRTBIZ_IDX = 1 AND C.TP_DEVLOP_SE = 'use' AND C.CONFM_STATUS = '55' AND TO_CHAR(C.LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND C.BPL_NO = A.BPL_NO) = 0 THEN 'N' ELSE 'Y' END as AI_STD_SOJT, CASE WHEN (SELECT COUNT(*) FROM HRD_DEVLOP C, HRD_DEVLOP_TP D WHERE C.DEVLOP_IDX = D.DEVLOP_IDX AND D.PRTBIZ_IDX = 4 AND C.TP_DEVLOP_SE IN ('reuse', 'new') AND C.CONFM_STATUS = '55' AND TO_CHAR(C.LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND C.BPL_NO = A.BPL_NO) = 0 THEN 'N' ELSE 'Y' END as AI_SLF_BPR, CASE WHEN (SELECT COUNT(*) FROM HRD_DEVLOP C, HRD_DEVLOP_TP D WHERE C.DEVLOP_IDX = D.DEVLOP_IDX AND D.PRTBIZ_IDX = 1 AND C.TP_DEVLOP_SE IN ('reuse', 'new') AND C.CONFM_STATUS = '55' AND TO_CHAR(C.LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND C.BPL_NO = A.BPL_NO) = 0 THEN 'N' ELSE 'Y' END as AI_SLF_SOJT, CASE WHEN (SELECT COUNT(*) FROM HRD_CNSL c WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 1 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = '0' THEN 'N' ELSE 'Y' END AS FIT_BPR, CASE WHEN (SELECT COUNT(*) FROM HRD_CNSL c WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 2 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = '0' THEN 'N' ELSE 'Y' END AS FIT_SOJT, CASE WHEN (SELECT COUNT(*) FROM HRD_CNSL c WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 3 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = '0' THEN 'N' ELSE 'Y' END AS FIT_SOJT_SITE, CASE WHEN (SELECT COUNT(*) FROM HRD_BSK_BPR C WHERE BPL_NO = A.BPL_NO AND TR_MTH = 'S-OJT훈련' AND TR_MBY IN ('생산문제해결형','K디지털형','신기술융합형') AND TO_CHAR(TR_START_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM')) = '0' THEN 'N' ELSE 'Y' END AS SLF_TR_BPR , CASE WHEN (SELECT COUNT(*) FROM HRD_BSK_BPR C WHERE BPL_NO = A.BPL_NO AND TR_MTH = 'S-OJT훈련' AND MIX_TR_MTH IN ('장기훈련','기본훈련') AND TO_CHAR(TR_START_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM')) = '0' THEN 'N' ELSE 'Y' END AS SLF_TR_SOJT, CASE WHEN (SELECT COUNT(*) FROM HRD_BSK_BPR C WHERE BPL_NO = A.BPL_NO AND TR_MBY IN ('자체','자체+위탁') AND TO_CHAR(TR_START_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM')) = '0' THEN 'N' ELSE 'Y' END AS SLF_TR_SOJT_SITE, '-' AS SLF_TR_WLB, CASE WHEN (SELECT COUNT(*) FROM HRD_BSK_BPR C WHERE BPL_NO = A.BPL_NO AND TR_MBY = '위탁' AND TO_CHAR(TR_START_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM')) = '0' THEN 'N' ELSE 'Y' END AS CNSGN_TR_BRP, CASE WHEN (SELECT COUNT(*) FROM HRD_BSK_CON C WHERE BPL_NO = A.BPL_NO AND TR_MBY = '위탁' AND TO_CHAR(TR_START_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM')) = '0' THEN 'N' ELSE 'Y' END AS CNSGN_TR_CNSRTM, CASE WHEN (SELECT COUNT(*) FROM HRD_BSK_REG C WHERE BPL_NO = A.BPL_NO AND TR_MBY = '위탁' AND TO_CHAR(TR_START_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM')) = '0' THEN 'N' ELSE 'Y' END AS CNSGN_TR_RIB, CASE WHEN (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL_RSLT_ANALS C WHERE TR_RESULT_REPRT_SE = 1 AND STATUS = 1 AND ISDELETE = 0 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = '0' THEN 'N' ELSE 'Y' END AS RSLT_SNST, CASE WHEN (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL_RSLT_ANALS C WHERE TR_RESULT_REPRT_SE = 2 AND STATUS = 1 AND ISDELETE = 0 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = '0' THEN 'N' ELSE 'Y' END AS RSLT_APPLC, CASE WHEN (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL C WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 4 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = 0 THEN 'N' ELSE 'Y' END AS DEP_DGNS, CASE WHEN (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL C WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 5 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = 0 THEN 'N' ELSE 'Y' END AS EDU_TR_SYS, CASE WHEN (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL C WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 6 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = 0 THEN 'N' ELSE 'Y' END AS SITE_PRC FROM HRD_BSK A, (SELECT BPL_NO FROM HRD_DGNS_BSC GROUP BY BPL_NO) B WHERE A.BPL_NO = B.BPL_NO ) TOT WHERE 1=1 AND INSTT_IDX = #{INSTT_IDX}
AND STATISTICS_YEAR = TO_CHAR(SYSDATE, 'YYYY')
AND STATISTICS_YEAR = #{YEAR}
AND STATISTICS_MONTH = #{MONTH} AND CORP_TYPE LIKE #{TYPE} AND PRVTCNTR_NM LIKE #{CENTER} AND CORP_NM LIKE #{CORP} AND BPL_NO LIKE #{BPL}

SELECT COUNT(*) FROM ( SELECT TO_CHAR(SYSDATE,'YYYY') AS STATISTICS_YEAR, TO_CHAR(SYSDATE,'MM') AS STATISTICS_MONTH, A.INSTT_IDX AS INSTT_IDX, (SELECT INSTT_NAME FROM HRD_COM_INSTT C WHERE C.INSTT_IDX = A.INSTT_IDX) AS INSTT_NM, (SELECT LISTAGG(PRVTCNTR_NAME,', ') WITHIN GROUP(ORDER BY PRVTCNTR_NAME) AS PRVTCNTR_NAME FROM (SELECT PRVTCNTR_NAME FROM HRD_COM_PRVTCNTR C WHERE C.MEMBER_IDX IN (SELECT MEMBER_IDX FROM HRD_SPTJ_AGREM D WHERE D.BPL_NO = A.BPL_NO))) AS PRVTCNTR_NM, CASE WHEN A.PRI_SUP_CD = '2' THEN '대규모기업' WHEN A.PRI_SUP_CD = '1' THEN '우선지원기업' WHEN A.PRI_SUP_CD IS NULL THEN '-' END AS CORP_TYPE, A.BPL_NM AS CORP_NM, A.BPL_NO AS BPL_NO, 'Y' AS DGNS, CASE WHEN (SELECT COUNT(DISTINCT A.BPL_NO) FROM HRD_DGNS_QUSTNR_RSLT C, HRD_DGNS_BSC D WHERE C.BSC_IDX = D.BSC_IDX AND TO_CHAR(C.ISSUE_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND D.BPL_NO = A.BPL_NO ) = 0 THEN 'N' ELSE 'Y' END AS QESTNR, CASE WHEN (SELECT COUNT(*) FROM HRD_DGNS_BSISCNSL C WHERE STATUS = '1' AND TO_CHAR(ISSUE_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = 0 THEN 'N' ELSE 'Y' END AS BSISBNSL, CASE WHEN (SELECT COUNT(*) FROM HRD_DEVLOP C, HRD_DEVLOP_TP D WHERE C.DEVLOP_IDX = D.DEVLOP_IDX AND D.PRTBIZ_IDX = 4 AND C.TP_DEVLOP_SE = 'use' AND C.CONFM_STATUS = '55' AND TO_CHAR(C.LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND C.BPL_NO = A.BPL_NO) = 0 THEN 'N' ELSE 'Y' END as AI_STD_BPR, CASE WHEN (SELECT COUNT(*) FROM HRD_DEVLOP C, HRD_DEVLOP_TP D WHERE C.DEVLOP_IDX = D.DEVLOP_IDX AND D.PRTBIZ_IDX = 1 AND C.TP_DEVLOP_SE = 'use' AND C.CONFM_STATUS = '55' AND TO_CHAR(C.LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND C.BPL_NO = A.BPL_NO) = 0 THEN 'N' ELSE 'Y' END as AI_STD_SOJT, CASE WHEN (SELECT COUNT(*) FROM HRD_DEVLOP C, HRD_DEVLOP_TP D WHERE C.DEVLOP_IDX = D.DEVLOP_IDX AND D.PRTBIZ_IDX = 4 AND C.TP_DEVLOP_SE IN ('reuse', 'new') AND C.CONFM_STATUS = '55' AND TO_CHAR(C.LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND C.BPL_NO = A.BPL_NO) = 0 THEN 'N' ELSE 'Y' END as AI_SLF_BPR, CASE WHEN (SELECT COUNT(*) FROM HRD_DEVLOP C, HRD_DEVLOP_TP D WHERE C.DEVLOP_IDX = D.DEVLOP_IDX AND D.PRTBIZ_IDX = 1 AND C.TP_DEVLOP_SE IN ('reuse', 'new') AND C.CONFM_STATUS = '55' AND TO_CHAR(C.LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND C.BPL_NO = A.BPL_NO) = 0 THEN 'N' ELSE 'Y' END as AI_SLF_SOJT, CASE WHEN (SELECT COUNT(*) FROM HRD_CNSL c WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 1 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = '0' THEN 'N' ELSE 'Y' END AS FIT_BPR, CASE WHEN (SELECT COUNT(*) FROM HRD_CNSL c WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 2 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = '0' THEN 'N' ELSE 'Y' END AS FIT_SOJT, CASE WHEN (SELECT COUNT(*) FROM HRD_CNSL c WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 3 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = '0' THEN 'N' ELSE 'Y' END AS FIT_SOJT_SITE, CASE WHEN (SELECT COUNT(*) FROM HRD_BSK_BPR C WHERE BPL_NO = A.BPL_NO AND TR_MTH = 'S-OJT훈련' AND TR_MBY IN ('생산문제해결형','K디지털형','신기술융합형') AND TO_CHAR(TR_START_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM')) = '0' THEN 'N' ELSE 'Y' END AS SLF_TR_BPR , CASE WHEN (SELECT COUNT(*) FROM HRD_BSK_BPR C WHERE BPL_NO = A.BPL_NO AND TR_MTH = 'S-OJT훈련' AND MIX_TR_MTH IN ('장기훈련','기본훈련') AND TO_CHAR(TR_START_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM')) = '0' THEN 'N' ELSE 'Y' END AS SLF_TR_SOJT, CASE WHEN (SELECT COUNT(*) FROM HRD_BSK_BPR C WHERE BPL_NO = A.BPL_NO AND TR_MBY IN ('자체','자체+위탁') AND TO_CHAR(TR_START_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM')) = '0' THEN 'N' ELSE 'Y' END AS SLF_TR_SOJT_SITE, '-' AS SLF_TR_WLB, CASE WHEN (SELECT COUNT(*) FROM HRD_BSK_BPR C WHERE BPL_NO = A.BPL_NO AND TR_MBY = '위탁' AND TO_CHAR(TR_START_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM')) = '0' THEN 'N' ELSE 'Y' END AS CNSGN_TR_BRP, CASE WHEN (SELECT COUNT(*) FROM HRD_BSK_CON C WHERE BPL_NO = A.BPL_NO AND TR_MBY = '위탁' AND TO_CHAR(TR_START_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM')) = '0' THEN 'N' ELSE 'Y' END AS CNSGN_TR_CNSRTM, CASE WHEN (SELECT COUNT(*) FROM HRD_BSK_REG C WHERE BPL_NO = A.BPL_NO AND TR_MBY = '위탁' AND TO_CHAR(TR_START_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM')) = '0' THEN 'N' ELSE 'Y' END AS CNSGN_TR_RIB, CASE WHEN (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL_RSLT_ANALS C WHERE TR_RESULT_REPRT_SE = 1 AND STATUS = 1 AND ISDELETE = 0 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = '0' THEN 'N' ELSE 'Y' END AS RSLT_SNST, CASE WHEN (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL_RSLT_ANALS C WHERE TR_RESULT_REPRT_SE = 2 AND STATUS = 1 AND ISDELETE = 0 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = '0' THEN 'N' ELSE 'Y' END AS RSLT_APPLC, CASE WHEN (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL C WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 4 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = 0 THEN 'N' ELSE 'Y' END AS DEP_DGNS, CASE WHEN (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL C WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 5 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = 0 THEN 'N' ELSE 'Y' END AS EDU_TR_SYS, CASE WHEN (SELECT COUNT(DISTINCT C.BPL_NO) FROM HRD_CNSL C WHERE CONFM_STATUS = '55' AND CNSL_TYPE = 6 AND TO_CHAR(LAST_MODI_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM') AND A.BPL_NO = C.BPL_NO) = 0 THEN 'N' ELSE 'Y' END AS SITE_PRC FROM HRD_BSK A, (SELECT BPL_NO FROM HRD_DGNS_BSC GROUP BY BPL_NO) B WHERE A.BPL_NO = B.BPL_NO ) TOT WHERE 1=1 AND INSTT_IDX = #{INSTT_IDX}
AND STATISTICS_YEAR = TO_CHAR(SYSDATE, 'YYYY')
AND STATISTICS_YEAR = #{YEAR}
AND STATISTICS_MONTH = #{MONTH} AND CORP_TYPE LIKE #{TYPE} AND PRVTCNTR_NM LIKE #{CENTER} AND CORP_NM LIKE #{CORP} AND BPL_NO LIKE #{BPL}
